
public class Review04_Twos_Complement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte num1, num2;
		num1 = 10;
		num2 = -10;
		
		System.out.println(num1);
		System.out.println(num2);
		System.out.println();
		
		// Something New!
		num1 = 0b0000_1010;
		num2 = (byte)0B1111_0101;
		
		System.out.println(num1);
		System.out.println(num2);
		
	}

}
